        {
            var packet = new ConnectionRequest()
            {
                clientGuid = _clientGuid,
                timestamp = DateTime.UtcNow.Ticks / TimeSpan.TicksPerMillisecond,
                doSecurity = 0,
            };

            SendPackage(packet);
        }

